import React, { use } from "react";
import Header from "./Header";
import Footer from "./Footer";
import { useState, useEffect } from "react";

export default function ContactUs() {

  const [name , setName] = useState("");
  const [email , setEmail] = useState("");
  const [subject , setSubject] = useState("");
  const [textArea , setTextArea] = useState("");

  useEffect(async () => {
    try{
      const response = await fetch('http://localhost:3001/api/questions');
      const data = <Jso></Jso>
    }catch (err){
      console.log(err);
    }
  }, []);

  const handleClick = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, subject, message: textArea })
      });

      if (response.ok) {
        alert('Message sent!');
        setName('');
        setEmail('');
        setSubject('');
        setTextArea('');
      } else {
        alert('Failed to send message');
      }
    } catch (err) {
      console.error('Error:', err);
      alert('Error occurred');
    }
  }

  return (
    <div className="font-sans text-gray-800">

      {/* Contact Section */}
      <section className="max-w-6xl mx-auto px-6 py-16">
        <div className="grid md:grid-cols-2 gap-10">
          {/* Contact Form */}
          <div className="bg-gray-100 p-6 rounded shadow-md">
            <h2 className="text-xl font-semibold mb-4">Get in Touch</h2>
            <form className="space-y-4">
              <input
                type="text"
                placeholder="Your Name"
                className="w-full border border-gray-300 px-4 py-2 rounded"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
              <input
                type="email"
                placeholder="Your Email"
                className="w-full border border-gray-300 px-4 py-2 rounded"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="text"
                placeholder="Subject"
                className="w-full border border-gray-300 px-4 py-2 rounded"
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
              />
              <textarea
                rows="4"
                placeholder="Your Message"
                className="w-full border border-gray-300 px-4 py-2 rounded"
                value={textArea}
                onChange={(e) => setTextArea(e.target.value)}
              ></textarea>
              <button
                type="submit"
                className="w-full bg-black text-white py-2 rounded hover:bg-gray-800"
                onClick={handleClick}
              >
                Send Message
              </button>
            </form>
          </div>

          {/* Contact Info + Map */}
          <div>
            <h2 className="text-xl font-semibold mb-4">Contact Information</h2>
            <p>Email: <a href="mailto:contact@msmeconnect.com" className="text-blue-600 underline">contact@msmeconnect.com</a></p>
            <p>Phone: +1 234 567 890</p>

            <div className="flex space-x-4 mt-4">
              <a href="#"><i className="fab fa-facebook-f"></i></a>
              <a href="#"><i className="fab fa-twitter"></i></a>
              <a href="#"><i className="fab fa-linkedin-in"></i></a>
            </div>

            <h3 className="mt-6 mb-2 font-semibold">Our Location</h3>
            <img
              src="/map.png" // Replace with your actual image
              alt="Our Location"
              className="w-full rounded"
            />
          </div>
        </div>
      </section>

      {/* FAQ Section */}
      <section className="bg-gray-100 px-6 py-12">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-xl font-semibold mb-6">Frequently Asked Questions</h2>
          <div className="space-y-4">
            <div>
              <p className="font-semibold">Q: How can I become a member of MSME Connect?</p>
              <p className="text-gray-700">A: You can become a member by filling out the registration form available on our website.</p>
            </div>
            <div>
              <p className="font-semibold">Q: What services does MSME Connect provide?</p>
              <p className="text-gray-700">A: We offer services like business consultancy, networking, and resource access.</p>
            </div>
            <div>
              <p className="font-semibold">Q: Where is MSME Connect located?</p>
              <p className="text-gray-700">A: Our HQ is located in the heart of the business district, easily accessible by public transport.</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}
