import React, { use } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useEffect, useState } from "react";

export function Profile() {
    const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0();
    const [data , setData] = useState(null);
    const [ loading, setLoading ] = useState(true);
    const [error , setError ] = useState(null);

    useEffect(()=>{
        if(isAuthenticated){
            const fetchData = async () =>{
                try{
                    fetch(`http://localhost:3001/api/getLoan?email=${user.email}`)
                    .then((res) => {
                        if(!res.ok){
                            throw new Error("Network Issue");
                        }else{
                            return res.json();
                        }
                    })
                    .then((data) =>{
                        setData(data);
                    })

                }catch(error){
                    console.error(error.message);
                    setError(error.message);
                }finally{
                    setLoading(false);
                }
            }
            fetchData();
        }
    }, []);


    console.log(data);

    const rows = data?.rows[0]["amount"] || null;

    return (
        <>
            <div className="flex justify-end items-center gap-4 p-4">
                {isAuthenticated && user.name}
            </div>

            <div className="flex flex-col justify-start gap-4 rounded-xl shadow-md p-5 m-5 bg-gray-50">
                <h2 className="text-2xl font-bold border-b-2 pb-3 text-indigo-500">Your Loans</h2>
                {loading ? <p>Loading...</p> : 
                    <table border="1" cellPadding="8">
                    <thead>
                    <tr>
                        <th></th>
                        <th>Name</th>
                        <th>Role</th>
                    </tr>
                    </thead>
                    <tbody>
                    {rows.map(user => (
                        <tr key={user.id}>
                        <td>{user.id}</td>
                        <td>{user.name}</td>
                        <td>{user.role}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                }
            </div>
        </>
    );
}
