import React, { use } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useEffect, useState } from "react";

export function Profile() {
    const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0();
    const [data , setData] = useState(null);
    const [ loading, setLoading ] = useState(true);
    const [error , setError ] = useState(null);

    useEffect(()=>{
        if(isAuthenticated){
            const fetchData = async () =>{
                try{
                    const res = fetch(`http://localhost:3001/api/getLoan?email=${user.email}`)
                    if(!res.ok){
                        throw new Error("Network issue");
                    }

                }catch(error){
                    console.error(error.message);
                    setError(error.message);
                }finally{
                    setLoading(false);
                }
            }
            fetchData();
        }else{
            loginWithRedirect();
        }
    }, []);

    console.log(data.rows);

    const rows = data.rows;

    return (
        <>
            <div className="flex justify-end items-center gap-4 p-4">
                {isAuthenticated && user.name}
            </div>

            <div className="flex flex-col justify-start gap-4 rounded-xl shadow-md p-5 m-5 bg-gray-50">
                <h2 className="text-2xl font-bold border-b-2 pb-3 text-indigo-500">Your Loans</h2>
                {loading ? <p>Loading...</p> : 
                    <table border="1" cellPadding="8">
                    <thead>
                    <tr>
                        <th>Loan</th>
                        <th>Amount</th>
                        <th>Date</th>
                    </tr>
                    </thead>
                    <tbody>
                    {rows.map(user => (
                        <tr key={user.id}>
                        <td>{user.loan}</td>
                        <td>{user.amount}</td>
                        <td>{user.date}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                }
            </div>
        </>
    );
}
