import React, {useRef} from "react";
import { Link } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import { Services } from "./Services";
import { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

function Homepage(){

    const { loginWithRedirect , logout,  user, isAuthenticated } = useAuth0();
    const location = useLocation();

    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);

    return (
    <div className="font-sans text-gray-800">
      {/* Hero Section */}
      <div
        className="bg-cover bg-center relative"
        style={{
          backgroundImage: 'url("/hero-image2.jpg")',
        }}
      >
        <section className="h-[60vh] flex items-center justify-center text-center text-white">
          <div className="bg-black bg-opacity-50 p-8 rounded-lg shadow-lg transform transition duration-700 ease-out animate-fadeInUp">
            <h2 className="text-5xl font-extrabold mb-4 tracking-wide drop-shadow-lg">
              Bring Your Business Online
            </h2>
            <p className="mb-6 text-lg opacity-90">
              Unlock new opportunities and reach a broader audience with MSME
              Connect.
            </p>
            <div className="space-x-4">
              {isAuthenticated ? (
                <button
                  onClick={() =>
                    logout({ logoutParams: { returnTo: window.location.origin } })
                  }
                  className="bg-gradient-to-r from-gray-900 to-gray-700 text-white px-5 py-2 rounded-full shadow hover:scale-105 transform transition duration-300"
                >
                  Logout
                </button>
              ) : (
                <button
                  onClick={() =>
                    loginWithRedirect({
                      appState: { returnTo: location.pathname },
                    })
                  }
                  className="bg-gradient-to-r from-gray-900 to-gray-700 text-white px-5 py-2 rounded-full shadow hover:scale-105 transform transition duration-300"
                >
                  Sign Up
                </button>
              )}
              <Link
                to="/contact"
                className="bg-white text-black px-5 py-2 rounded-full shadow hover:bg-gray-200 hover:scale-105 transform transition duration-300"
              >
                Learn More
              </Link>
            </div>
          </div>
        </section>
      </div>

      {/* Tagline Section */}
      <div className="flex flex-col justify-center items-center pt-10 pb-10 text-3xl animate-fadeIn">
        <h1 className="font-bold tracking-wide">छोटे कदम, बड़ी उड़ान</h1>
        <img
          src="https://gorgeous-gumption-a148f7.netlify.app/pakshi.jpeg"
          alt="Tagline"
          className="pt-4 pb-4 rounded-lg shadow-lg hover:scale-105 transform transition duration-500"
        />
        <p className="text-lg italic opacity-80">
          (Chhote Kadam, Badi Udaan) - Small Steps, Big Flight
        </p>
      </div>

      {/* Success Stories */}
      <section className="py-12 bg-gray-100">
        <h3 className="text-3xl font-semibold text-center mb-8 animate-fadeIn">
          Success Stories
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 px-6 max-w-6xl mx-auto">
          {[
            {
              name: "Maria Lopez",
              text: "Thanks to MSME Connect, my business has grown exponentially!",
              image: "/story1.jpg",
            },
            {
              name: "John Smith",
              text: "Our reach has expanded far beyond our expectations.",
              image: "/story2.jpg",
            },
            {
              name: "Ahmed Khan",
              text: "MSME Connect made digital transformation seamless.",
              image: "/story3.jpg",
            },
          ].map((story, idx) => (
            <div
              key={idx}
              className="bg-white p-6 shadow-lg rounded-lg hover:shadow-2xl transform hover:scale-105 transition duration-500"
            >
              <img
                src={story.image}
                alt={story.name}
                className="mb-4 w-full h-48 object-cover rounded-lg hover:scale-105 transform transition duration-500"
              />
              <p className="mb-2 text-gray-700 italic">"{story.text}"</p>
              <p className="font-semibold">{story.name}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonials */}
      <section className="py-12 bg-gray-50">
        <h3 className="text-3xl font-semibold text-center mb-8 animate-fadeIn">
          Testimonials
        </h3>
        <div className="flex flex-col md:flex-row justify-center items-center gap-10 px-4">
          {[
            {
              name: "Emily Davis",
              text: "A game-changer for our business!",
              image: "/test1.jpg",
            },
            {
              name: "Michael Nguyen",
              text: "Highly recommend MSME Connect!",
              image: "/test2.jpg",
            },
          ].map((testimonial, idx) => (
            <div
              key={idx}
              className="text-center max-w-sm p-6 bg-white rounded-lg shadow-md hover:shadow-xl transition transform hover:scale-105 duration-500"
            >
              <img
                src={testimonial.image}
                alt={testimonial.name}
                className="w-20 h-20 mx-auto rounded-full mb-4 border-4 border-gray-200 hover:rotate-6 transition-transform duration-500"
              />
              <p className="italic text-gray-700">"{testimonial.text}"</p>
              <p className="mt-2 font-semibold">{testimonial.name}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Homepage;
