import React, {useRef} from "react";
import Header from "./Header";
import Footer from "./Footer";
import ServiceCard from "./ServiceCard";
import { Link } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";

function Homepage(){

    const { loginWithRedirect , logout,  user, isAuthenticated } = useAuth0();

    return (
        <div className="font-sans text-gray-800">
            {/* Hero-Section */}
            <div className="bg-cover bg-center bg-[image:var(--bg-width)] " style={{ backgroundImage: 'url("/hero-image2.jpg")', '--bg-width' : '70vw' }}>
                <section className="h-[60vh] flex items-center justify-center text-center text-white" >
                <div className="bg-black bg-opacity-50 p-8 rounded">
                    <h2 className="text-4xl font-bold mb-4">Bring Your Business Online</h2>
                    <p className="mb-6">Unlock new opportunities and reach a broader audience with MSME Connect.</p>
                    <div className="space-x-4">
                    {isAuthenticated ? 
                    <button onClick={e => logout({ logoutParams: { returnTo: window.location.origin } })} className="bg-black text-white px-4 py-2 rounded hover:bg-gray-800">Logout</button> : 
                    <button onClick={e => loginWithRedirect()} className="bg-black text-white px-4 py-2 rounded hover:bg-gray-800">Sign Up</button>}
                    <Link to="/contact" className="bg-white text-black px-4 py-2 rounded hover:bg-gray-100">Learn More</Link>
                    </div>
                </div>
                </section>
            </div>

            <div className="flex flex-col justify-center items-center pt-6 pb-6 text-2xl">
                <h1>
                    छोटे कदम, बड़ी उड़ान
                </h1>
                <img src="https://gorgeous-gumption-a148f7.netlify.app/pakshi.jpeg" alt="Tagline Image" className="pt-2 pb-4" />
                <p class="tagline-translation">
                    (Chhote Kadam, Badi Udaan) - Small Steps, Big Flight
                </p>
            </div>

            {/* Success Stories */}
            <section className="py-12 bg-gray-100">
                <h3 className="text-2xl font-semibold text-center mb-8">Success Stories</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 px-4 max-w-6xl mx-auto">
                {[
                    { name: "John Smith", text: "Thanks to MSME Connect, my business has grown exponentially!", image: "/story1.jpg" },
                    { name: "Maria Lopez", text: "Our reach has expanded far beyond our expectations.", image: "/story2.jpg" },
                    { name: "Ahmed Khan", text: "MSME Connect made digital transformation seamless.", image: "/story3.jpg" }
                ].map((story, idx) => (
                    <div key={idx} className="bg-white p-6 shadow rounded">
                    <img src={story.image} alt={story.name} className="mb-4 w-full h-40 object-cover rounded" />
                    <p className="mb-2">"{story.text}"</p>
                    <p className="font-semibold">- {story.name}</p>
                    </div>
                ))}
                </div>
            </section>

            {/* Services */}
            <section className="py-12 scroll-mt-24 bg-[#FBFBFB]" id="target">
                <div className="flex flex-col justify-center items-center">
                    <h2 className="font-bold text-2xl pb-6">Your Partner in Business Growth</h2>
                    <p className="text-center w-60">
                        Welcome to the platform designed to be your trusted partner for managing and growing your business. We understand the challenges you face every day, from managing finances to finding the right resources. Our goal is to simplify these complexities so you can focus on what you do best: building your dream.
                    </p>
                </div>
                <h3 className="text-2xl font-semibold text-center mb-8">Our Services</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-8 px-4 max-w-5xl mx-auto text-center">
                <ServiceCard 
                    svg={
                            <img src="https://gorgeous-gumption-a148f7.netlify.app/term.png" alt="Business Terminology" />
                        }
                    heading={<h3>Business Terminology</h3>}
                    description={<p>We help you understand complex or tricky business terms, giving you the knowledge to navigate the corporate world with confidence.</p>}
                />
                <ServiceCard 
                    svg={
                            <img src="https://gorgeous-gumption-a148f7.netlify.app/loan.png" alt="Loan &amp; Credit" />
                        }
                    heading={<h3>Loan &amp; Credit Management</h3>}
                    description={<p>Easily manage your loans, track your credit score, and discover government schemes and NGOs that offer affordable loans.</p>}
                />
                <ServiceCard 
                    svg={
                            <img src="https://gorgeous-gumption-a148f7.netlify.app/operations.png" alt="Operations"/>
                        }
                    heading={<h3>Operations Management</h3>}
                    description={<p>Tools to manage your business operations, including inventory, sales, and worker count, giving you a clear overview of your performance.</p>}
                />
                <ServiceCard 
                    svg={
                        <img src="https://gorgeous-gumption-a148f7.netlify.app/wholesale.png" alt="Wholesalers"/>
                    }
                    heading={<h3>Local Wholesalers Network</h3>}
                    description={<p>Connect with nearby wholesalers and suppliers. Our network makes it easy to find and source the products you need efficiently.</p>}
                />
                <ServiceCard 
                    svg={
                        <img src="https://gorgeous-gumption-a148f7.netlify.app/market.png" alt="Marketing"/>
                    }
                    heading={<h3>Marketing Solutions</h3>}
                    description={<p>Get discovered with our tailored marketing assistance, from creating digital campaigns to collaborating with influencers.</p>}
                />
                <ServiceCard 
                    svg={
                        <img src="https://gorgeous-gumption-a148f7.netlify.app/certificate.png" alt="Licenses"/>
                    }
                    heading={<h3>Licenses &amp; Certificates</h3>}
                    description={<p>Manage all your existing licenses and get a personalized list of required certifications based on your business type and turnover.</p>}
                />
                </div>
            </section>

            {/* Testimony */}
            <section className="py-12 bg-gray-100">
                <h3 className="text-2xl font-semibold text-center mb-8">Testimonials</h3>
                <div className="flex flex-col md:flex-row justify-center items-center gap-8 px-4">
                <div className="text-center max-w-sm">
                    <img src="/test1.jpg" alt="Emily Davis" className="w-16 h-16 mx-auto rounded-full mb-2" />
                    <p className="italic">"A game-changer for our business!" – Emily Davis</p>
                </div>
                <div className="text-center max-w-sm">
                    <img src="/test2.jpg" alt="Michael Nguyen" className="w-16 h-16 mx-auto rounded-full mb-2" />
                    <p className="italic">"Highly recommend MSME Connect!" – Michael Nguyen</p>
                </div>
                </div>
            </section>
        </div>
    );
}

export default Homepage;
