import React, { useEffect, useState } from "react";

export default function Tips(){

    const [ error, setError] = useState(null);
    const [ loading, setLoading] = useState(true);
    const [ data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async ()=> {
            try{
                const respose = await fetch('http://localhost:3001/api/tips');
                const data = await respose.json();
                setData(data);
            }catch (err){
                alert("Error Occured", err);
                setError(err);
            }finally{
                setLoading(false);
            }
        }
        fetchData();
    }, []);

    const tips = data.length ? (data[Math.floor(Math.random() * data.length)]) : null;

    return (
        <>
            {!loading && error ? <p> error occured </p> :
                <div className="rounded-xl shadow-xl flex justify-center itmes-center z-40">
                    <div className="flex flex-col justify-center items-center bg-gray-300 rounded-xl">
                        <p>{tips?.tip}</p>
                    </div>
                </div>
            }
        </>
    );
}
