import React, { use } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

export function Profile() {
    const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0();
    const [data , setData] = useState(null);
    const [ loading, setLoading ] = useState(true);
    const [error , setError ] = useState(null);
    const location = useLocation();

    useEffect(()=>{
        if(isAuthenticated){
            const fetchData = async () =>{
                try{
                    const res = await fetch(`http://localhost:3001/api/getLoan`)
                    if(!res.ok){
                        throw new Error("Network issue");
                    }else{
                        const json = await res.json();
                        setData(json);
                    }

                }catch(error){
                    console.error(error.message);
                    setError(error.message);
                }finally{
                    setLoading(false);
                }
            }
            fetchData();
        }else{
            loginWithRedirect({
                appState: { returnTo: location.pathname }
            });
        }
    }, [isAuthenticated]);

    console.log(data);

    return (
        <>
            {/* Profile Header */}
            <div className="mt-9 pt-9">
            <div className="flex justify-end items-center gap-3 px-6 py-3 bg-white shadow-md rounded-xl ml-auto">
                <span className="font-medium text-gray-700">{user.name}</span>
                <img
                src={user.picture}
                alt="Profile"
                className="w-10 h-10 rounded-full border-2 border-indigo-400 shadow-sm"
                />
            </div>
            </div>

            {/* Loan Section */}
            <div className="flex flex-col justify-start gap-4 rounded-xl shadow-lg p-6 m-5 bg-white border border-gray-100">
            <h2 className="text-2xl font-bold border-b pb-3 text-indigo-600">
                Your Loans
            </h2>

            {loading ? (
                <div className="flex justify-center items-center py-10">
                <div className="w-6 h-6 border-4 border-indigo-500 border-t-transparent rounded-full animate-spin"></div>
                </div>
            ) : error ? (
                <p className="text-red-500 font-medium">Error: {error}</p>
            ) : data && data.length > 0 ? (
                <div className="overflow-x-auto">
                <table className="min-w-full bg-white rounded-lg shadow-sm border border-gray-200">
                    <thead className="bg-indigo-500 text-white">
                    <tr>
                        <th className="py-3 px-6 text-left">Loan</th>
                        <th className="py-3 px-6 text-left">Amount (₹)</th>
                        <th className="py-3 px-6 text-left">Date</th>
                    </tr>
                    </thead>
                    <tbody>
                    {data.map((loan, index) => (
                        <tr
                        key={loan.id}
                        className={`${
                            index % 2 === 0 ? "bg-gray-50" : "bg-white"
                        } hover:bg-indigo-50 transition`}
                        >
                        <td className="py-3 px-6">{loan.loan}</td>
                        <td className="py-3 px-6 font-medium text-gray-800">
                            ₹{loan.amount.toLocaleString()}
                        </td>
                        <td className="py-3 px-6 text-gray-600">{(loan.date).toLocaleDateString("en-CA")}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                </div>
            ) : (
                <p className="text-gray-500">No loans found.</p>
            )}
            </div>
        </>
        );

}
