import pg from "pg";

const db = new pg.Client({
    user: "postgres",
    host: "localhost",
    database: "world",
    password: "pchykj",
    port: 5432
});

export async function setData(name, email, subject, message) {
    const input = [name, email, subject, message];

    await db.query(`INSERT INTO MSME_dtata VALUES($1, $2, $3, $4)`, input);
}
