import React, {useRef} from "react";
import { Link } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import { Services } from "./Services";
import { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Tips from "./Tips";

function Homepage(){

    const { loginWithRedirect , logout,  user, isAuthenticated } = useAuth0();
    const location = useLocation();

    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);

    return (
    <div className="font-sans text-gray-900">
      {/* Modern Hero */}
      <section
        className="relative min-h-screen bg-cover bg-center flex items-center justify-center mt-9 pt-9"
        style={{ backgroundImage: 'url("/hero-image2.jpg")' }}
      >
        {/* Glassmorphism overlay */}
        <div className="absolute inset-0 bg-black/40 backdrop-blur-sm" />

        <div className="relative z-10 max-w-3xl text-center text-white px-6 animate-fadeInUp">
          <h1 className="text-5xl md:text-6xl font-extrabold bg-gradient-to-r from-purple-400 to-pink-500 bg-clip-text text-transparent">
            Bring Your Business Online
          </h1>
          <p className="mt-4 text-lg md:text-xl opacity-90">
            Unlock new opportunities and reach a broader audience with Solicio.
          </p>
          <div className="mt-8 flex flex-wrap gap-4 justify-center">
            {isAuthenticated ? (
              <button
                onClick={() =>
                  logout({ logoutParams: { returnTo: window.location.origin } })
                }
                className="px-6 py-3 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg hover:scale-105 transition-transform duration-300"
              >
                Logout
              </button>
            ) : (
              <button
                onClick={() =>
                  loginWithRedirect({
                    appState: { returnTo: location.pathname },
                  })
                }
                className="px-6 py-3 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg hover:scale-105 transition-transform duration-300"
              >
                Sign Up
              </button>
            )}
            <Link
              to="/contact"
              className="px-6 py-3 rounded-full bg-white text-gray-900 shadow-lg hover:scale-105 transition-transform duration-300"
            >
              Learn More
            </Link>
          </div>
        </div>
      </section>

      {/* Tagline */}
      <div className="py-16 text-center">
        <h2 className="text-3xl md:text-4xl font-semibold tracking-wide">
          छोटे कदम, बड़ी उड़ान
        </h2>
        <img
          src="https://gorgeous-gumption-a148f7.netlify.app/pakshi.jpeg"
          alt="Tagline"
          className="mt-6 w-56 rounded-xl shadow-lg hover:scale-105 transition duration-500 mx-auto"
        />
        <p className="mt-4 text-gray-500 italic">
          (Chhote Kadam, Badi Udaan) - Small Steps, Big Flight
        </p>
      </div>

      {/* Success Stories */}
      <section className="py-20 bg-gray-50">
        <h3 className="text-4xl font-bold text-center mb-12">
          Success Stories
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-10 px-6 max-w-6xl mx-auto">
          {[
            {
              name: "Maria Lopez",
              text: "Thanks to MSME Connect, my business has grown exponentially!",
              image: "/story1.jpg",
            },
            {
              name: "John Smith",
              text: "Our reach has expanded far beyond our expectations.",
              image: "/story2.jpg",
            },
            {
              name: "Ahmed Khan",
              text: "MSME Connect made digital transformation seamless.",
              image: "/story3.jpg",
            },
          ].map((story, idx) => (
            <div
              key={idx}
              className="bg-white rounded-2xl shadow-md hover:shadow-xl transform hover:scale-105 transition duration-500 overflow-hidden"
            >
              <img
                src={story.image}
                alt={story.name}
                className="w-full h-56 object-cover"
              />
              <div className="p-6">
                <p className="mb-3 text-gray-600 italic">"{story.text}"</p>
                <p className="font-semibold text-lg">{story.name}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonials */}
      <section className="py-20 bg-white">
        <h3 className="text-4xl font-bold text-center mb-12">
          Testimonials
        </h3>
        <div className="flex flex-col md:flex-row justify-center items-center gap-12 px-6">
          {[
            {
              name: "Emily Davis",
              text: "A game-changer for our business!",
              image: "/test1.jpg",
            },
            {
              name: "Michael Nguyen",
              text: "Highly recommend MSME Connect!",
              image: "/test2.jpg",
            },
          ].map((testimonial, idx) => (
            <div
              key={idx}
              className="bg-gray-50 p-8 rounded-2xl shadow-md hover:shadow-xl transition duration-500 text-center max-w-sm"
            >
              <img
                src={testimonial.image}
                alt={testimonial.name}
                className="w-20 h-20 mx-auto rounded-full border-4 border-white shadow-md hover:scale-110 transition-transform duration-500"
              />
              <p className="mt-4 italic text-gray-600">"{testimonial.text}"</p>
              <p className="mt-3 font-semibold text-lg">{testimonial.name}</p>
            </div>
          ))}
        </div>
      </section>

      <Tips />
    </div>
  );
}

export default Homepage;
