import { Link, NavLink } from "react-router-dom";

function Header (){
    return (
        <div className="sticky top-0 bg-[#9ECAD6] w-full">
            <header className="flex justify-between items-center p-6 shadow-xl text-[#000]">
                <h1 className="text-xl font-bold">MSME Connect</h1>
                <nav className="space-x-6">
                <NavLink to="/" className={({isActive}) => 
                    `hover:underline hover-bg-gray-50 ${isActive ? "text-orange-700" : "text-gray-700"}`
                }>Home</NavLink>
                <NavLink to="/contact" className={({isActive}) => 
                    `hover:underline hover-bg-gray-50 ${isActive ? "text-orange-700" : "text-gray-700"}`
                }>Contact Us</NavLink>
                <NavLink to="/about" className={({isActive}) =>
                    `hover:underline hover-bg-gray-50 ${isActive ? "text-orange-700" : "text-gray-700"}`
                }>About Us</NavLink>
                </nav>
            </header>
        </div>
    );
}

export default Header;
